        "    } ",
        "    a.header_row_element:hover { ",
        "        background: var(--background-color); ",
        "    } ",
        "    #chat_hbar, #header_row2.header_row { ",
        "        background: var(--background-color); ",
        "        color: var(--text-color); ",
        "    } ",
        "    .dropdown_item { ",
        "        background: var(--background-color); ",
        "        color: var(--text-color)!important; ",
        "    } ",
        "    .dropdown_item:hover { ",
        "        background: var(--background-color); ",
        "    } ",
